import React from'react';
import'../Form.css';
import { Input } from'reactstrap';
import'bootstrap/dist/css/bootstrap.min.css';
import { Component,state, handleStringChange, submitRegister } from "react";
import axios from'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';

import {toast} from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';
toast.configure(); 

class Login extends Component{

    constructor(props) {
        super(props)
        this.state = {
            userEmailAddress : "",
            userPassword : ""
        }
    }
    
    handleStringChange =(e)=>{
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    handleSubmitLogin = (e)=>{
        
        e.preventDefault();
        axios.post("http://localhost:9000/user/login", {
            userEmailAddress:this.state.userEmailAddress,
            userPassword: this.state.userPassword
        })
        .then((response)=>{
            if(validateForm()==null){
            console.log(response);
            localStorage.setItem('token',response.data.token);
            localStorage.setItem('userEmailAddress',response.data.userEmailAddress);
            toast('Welcome ' + this.state.userEmailAddress)

            this.props.history.push('/');
            }
        })        
        .catch((error)=>{
            if(validateForm()==null){
            toast('ERROR USER LOGIN')
            console.log(error.response)
            }
        })
    }


    render(){
        return(
            <div>
                <Header></Header>
                 <div class="hero-area">
    	<div class="page-banner parallax">
        	<div class="container">
            	<div class="page-banner-text">
        			<h1 class="block-title">Login</h1>
                </div>
            </div>
        </div>
    </div>
    <div class="login-container"><div class="block-title-nb">
   Please Sign In</div>
<div class="block block-customer-login col-xs-12 col-lg-6">
   <div class="block-title">
      <strong id="block-customer-login-heading" role="heading" aria-level="2">Registered Customers</strong>
   </div>
   <div class="block-content" aria-labelledby="block-customer-login-heading">
      <form class="form form-login"
         action="https://demo3.cmsmart.net/mag2_megashop_themeforest/default/customer/account/loginPost/"
         method="post"
         id="login-form"
         data-mage-init='{"validation":{}}'>
         <input name="form_key" type="hidden" value="cD2kHo3emXedfKU2" />         <fieldset class="fieldset login" data-hasrequired="* Required Fields">
            <div class="field note">If you have an account, sign in with your email address.</div>
            <div class="field email required">
               <label class="label" for="email"><span>Email</span></label>
               <div class="control">
                  <input placeholder="Email" name="login[username]" value=""  autocomplete="off" id="email" type="email" class="input-text" title="Email" data-validate="{required:true, 'validate-email':true}"/>
               </div>
            </div>
            <div class="field password required">
               <label for="pass" class="label"><span>Password</span></label>
               <div class="control">
                  <input placeholder="Password" name="login[password]" type="password"  autocomplete="off" class="input-text" id="pass" title="Password" data-validate="{required:true}"/>
               </div>
            </div>
            <div class="field-recaptcha" id="msp-recaptcha-container" data-bind="scope:'msp-recaptcha'">
</div>

            <div class="actions-toolbar">
               <div class="primary"><button type="submit" class="action login primary" name="send" id="send2"><span>Sign In</span></button></div>
               <div class="secondary"><a class="action remind" href="../forgotpassword/index.html"><span>Forgot Your Password?</span></a></div>
            </div>
         </fieldset>
      </form>
   </div>
</div><div class="block block-new-customer  col-xs-12 col-lg-6">
    <div class="block-title">
        <strong id="block-new-customer-heading" role="heading" aria-level="2">New Customers</strong>
    </div>
    <div class="block-content" aria-labelledby="block-new-customer-heading">
        <p>
        Creating an account has many benefits: check out faster, keep more than one address, track orders and more.            <ul>
                <li>Check out faster</li>
                <li>Keep more than one address</li>
                <li>Track orders</li>
                <li>Track wishlist and more</li>
            </ul>
        </p>
        <div class="actions-toolbar">
            <div class="primary">
                <a href="../create/index.html" class="action create primary"><span>Create an Account</span></a>
            </div>
        </div>
    </div>
</div>
</div>
            <div class="form">
                <div class="container">
                    <div class="row justify-content-center">
                        <div class="col-xl-7 col-lg-8 col-md-8">
                            <div class="section-title">
                                <h2>Login Now</h2></div>
                            </div>
                    </div>
                    <div class="row justify-content-center">
                        <div class="col-xl-9 col-lg-9">
                            <div class="all-form">
                                <div class="single-form first-step" >
                                    <form id="LoginForm">
                                        <div class="row">
                                            
                                                <p class="labelAlternative">Email Address</p>
                                                <input type="text" name="userEmailAddress" id="userEmailAddress" value={this.state.userEmailAddress} onChange={this.handleStringChange} required/>
                                            
                                        
                                        </div>

                                        <div class="row">
                                            
                                                <p class="labelAlternative">Password</p>
                                                <input type="text" name="userPassword" id="userPassword" value={this.state.userPassword} onChange={this.handleStringChange} required/>
                                            
                                        
                                        </div>

                                        <button class="next" type="submit" onClick={this.handleSubmitLogin}>LOGIN</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        <Footer></Footer>
        </div>
        )
    }
}


function validateForm() {
    
    var userEmailAddress = document.forms["LoginForm"]["userEmailAddress"].value;
    if (userEmailAddress == "") {
      alert("Username must be filled out");
      return false;
    }

    var userPassword = document.forms["LoginForm"]["userPassword"].value;
    if (userPassword == "") {
      alert("Password must be filled out");
      return false;
    }
    
}


export default Login;
