import React from'react';
import'./Form.css';
import { Input } from'reactstrap';
import'bootstrap/dist/css/bootstrap.min.css';
import { Component,state, handleStringChange, submitRegister } from "react";
import axios from'axios';

import {toast} from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
toast.configure(); 

class Login extends Component{

    constructor(props) {
        super(props)
        this.state = {
            userUsername : "",
            userPassword : ""
        }
    }
    
    changeHandler =(e)=>{
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    submitLogin = (e)=>{
        
        e.preventDefault();
        axios.post("http://localhost:9000/user/login", {
            userUsername:this.state.userUsername,
            userPassword: this.state.userPassword
        })
        .then((response)=>{
            if(validateForm()==null){
            console.log(response);
            localStorage.setItem('token',response.data.token);
            localStorage.setItem('userUsername',response.data.userUsername);
            toast('Welcome ' + this.state.userUsername)

            this.props.history.push('/');
            }
        })        
        .catch((error)=>{
            if(validateForm()==null){
            toast('ERROR USER LOGIN')
            console.log(error.response)
            }
        })
    }


    render(){
        return(
            <div>
            <div class="form">
                <div class="container">
                    <div class="row justify-content-center">
                        <div class="col-xl-7 col-lg-8 col-md-8">
                            <div class="section-title">
                                <h2>Login Now</h2></div>
                            </div>
                    </div>
                    <div class="row justify-content-center">
                        <div class="col-xl-9 col-lg-9">
                            <div class="all-form">
                                <div class="single-form" id="first-step">
                                    <form>
                                        <div class="row">
                                            <div class="col">
                                                <p class="labelAlternative">Match League</p>
                                                <input type="text" name="matchLeague" value={this.state.matchLeague} onChange={this.handleStringChange} required/>
                                            </div>
                                            <div class="col">
                                                <p class="labelAlternative">Match Venue</p>
                                                <input type="text" name="matchVenue" value={this.state.matchVenue} onChange={this.handleStringChange} required/>
                                            </div>
                                        </div>

                                        <div class="row">
                                            <div class="col">
                                                <p class="labelAlternative">Match Date</p>
                                                <input type="date" name="matchDate" value={this.state.matchDate} onChange={this.handleStringChange} required/>
                                            </div>
                                            <div class="col">
                                                <p class="labelAlternative">Match Time</p>
                                                <input type="time" name="matchTime" value={this.state.matchTime} onChange={this.handleStringChange} required/>
                                            </div>
                                        </div>

                                        <div class="row">
                                            <div class="col">
                                                <p class="labelAlternative">Home Team</p>
                                                <input type="text" name="homeTeam" value={this.state.homeTeam} onChange={this.handleStringChange} required/>
                                            </div>
                                            <div class="col">
                                                <p class="labelAlternative">Away Team</p>
                                                <input type="text" name="awayTeam" value={this.state.awayTeam} onChange={this.handleStringChange} required/>
                                            </div>
                                        </div>

                                        <div class="row">
                                            <div class="col">
                                                <p class="labelAlternative">Home Team Score</p>
                                                <input type="number" name="homeTeamScore" value={this.state.homeTeamScore} onChange={this.handleStringChange} required/>
                                            </div>
                                            <div class="col">
                                                <p class="labelAlternative">Away Team Score</p>
                                                <input type="number" name="awayTeamScore" value={this.state.awayTeamScore} onChange={this.handleStringChange} required/>
                                            </div>
                                        </div>

                                        <div class="row">
                                            <div class="col">
                                                <p class="labelAlternative">Home Team Odds</p>
                                                <input type="number" name="homeTeamOdds" value={this.state.homeTeamOdds} onChange={this.handleStringChange} required/>
                                            </div>
                                           
                                            <div class="col">
                                                <p class="labelAlternative">Draw Odds</p>
                                                <input type="number"  name="drawOdds" value={this.state.drawOdds} onChange={this.handleStringChange} required/>
                                            </div>
                                            <div class="col">
                                                <p class="labelAlternative">Away Team Odds</p>
                                                <input type="number" name="awayTeamOdds" value={this.state.awayTeamOdds} onChange={this.handleStringChange} required/>
                                            </div>
                                        </div>
                                        
                                        <button class="next" type="submit" onClick={this.AddMatchActivity}>Next</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )
    }
}


function validateForm() {
    
    var userUsername = document.forms["myForm"]["userUsername"].value;
    if (userUsername == "") {
      alert("Username must be filled out");
      return false;
    }

    var userPassword = document.forms["myForm"]["userPassword"].value;
    if (userPassword == "") {
      alert("Password must be filled out");
      return false;
    }
    
}


export default Login;
