import '../LoginRegister.css';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import decode from 'jwt-decode';
import Header from '../components/Header';
import FeatureLinks from '../components/FeatureLinks';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

toast.configure();

class DEF extends Component{

    constructor(props) {
        super(props)
        this.state = {
            userEmailAddress : "",
            userPassword : ""
        }
    }
    
    handleStringChange =(e)=>{
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    handleSubmitLogin = (e)=>{
        
        e.preventDefault();
        axios.post("http://localhost:9000/user/login", {
            userEmailAddress:this.state.userEmailAddress,
            userPassword: this.state.userPassword
        })
        .then((response)=>{
            if(validateForm()==null){
            console.log(response);
            localStorage.setItem('token',response.data.token);
            localStorage.setItem('userEmailAddress',response.data.userEmailAddress);
            toast('Welcome ' + this.state.userEmailAddress)

            this.props.history.push('/');
            }
        })        
        .catch((error)=>{
            if(validateForm()==null){
            toast('ERROR USER LOGIN')
            console.log(error.response)
            }
        })
    }


render(){

return(
<div>
    <Header></Header>

<img class="wave" src="img/wave.svg"/>
    <div class="logincontainer">
        <div class="img">
            <img src="img/authentication.svg"/>
        </div>
        <div class="login-container">
            <form action="login.html" class="divform">
                <h2>Login</h2>
                <p>Welcome back!</p>
                <div class="input-div one">
                    <div class="i">
                        <i class="fas fa-user"></i>
                    </div>
                    <div>
                        <h5>Email Address</h5>
                        <input class="input"  name="userEmailAddress" id="userEmailAddress" value={this.state.userEmailAddress} onChange={this.handleStringChange} type="text"/>
                    </div>
                </div>
                <div class="input-div two">
                    <div class="i">
                        <i class="fas fa-key"></i>
                    </div>
                    <div>
                        <h5>Password</h5>
                        <input class="input"  name="userPassword" id="userPassword" value={this.state.userPassword} onChange={this.handleStringChange} type="password"/>
                    </div>
                </div>
                <input type="submit" class="btn" value="Login Now"/>
                <a class="forgot" href="forgotPassword.html">Forgot Your Password ?</a>
               
               
                <div class="account">
                    <p>Don't have an account yet ?</p>
                    <a href="register.html">Sign up</a>
                </div>
            </form>
        </div>
    </div>
    </div>

)
}

}

export default DEF;
